import React from "react";
import {Link} from "react-scroll"
import homeHeader from "../Images/HomeImg/HomeSampleimg1.webp";

function BBCPoliticsCard() {
  return (
    <div className="hero min-h-screen bg-base-100 shadow-2x1">
      <div className="hero-content flex-col lg:flex-row">
        <img className=" max-w-sm rounded-lg shadow-2xl" src={homeHeader} />
        <div>
          <h1 className="text-5xl font-bold">Hi I'm Archie! </h1>
          <p className="py-6">
            I'm a Computing Science graduate and aspiring UX designer. Have a
            look at some of my work and learn a bit about me, feel free to get
            in touch too!
          </p>
          <Link to="Study1" spy={true} smooth={true} offset={50} duration={500}>
            <button className="btn btn-primary">Check out my work!</button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default BBCPoliticsCard;
